	private void jButton_logoActionPerformed(java.awt.event.ActionEvent evt) {
		try {
			loginName = jTextField_id.getText();
			String password = new String(jPasswordField_password.getPassword());

			LoginThrows loginThrows = new LoginThrows(loginName, password);
			String result = loginThrows.login();
			jLabel_message.setText(result);
			if (result.equals("µÇÂ¼³É¹¦")) {
				ManagerFrame main = new ManagerFrame();
				main.setVisible(true);
				this.dispose();
			}
		} catch (Exception e) {
			jLabel_message.setText(e.getMessage());
			e.printStackTrace();
		}
	}

	private void jButton_returnActionPerformed(java.awt.event.ActionEvent evt) {
		this.dispose();
		MainFrame mainFrame = new MainFrame();
		mainFrame.setVisible(true);
	}

