	private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {
		int rowInView = jTable1.getSelectedRow();
		int rowIndata = jTable1.convertRowIndexToModel(rowInView);
		record = dataModel.getRow(rowIndata);
	}

