	private void jButton_inquireActionPerformed(java.awt.event.ActionEvent evt) throws ClassNotFoundException, SQLException {
		if(jTextField_ISBN.getText().equals("")){
			dataModel.showNewRows(dao.getAll());
		}else{
			dataModel.showNewRows(dao.getBookInquire(jTextField_ISBN.getText()));
		}
		
	}

	private void jButton_returnActionPerformed(java.awt.event.ActionEvent evt) {
		this.dispose();
		StudentFrame studentFrame = new StudentFrame();
		studentFrame.setVisible(true);
	}

